import React from 'react';
import {useState} from 'react';

function Button() {
    const [count, setCount] = useState(0);

    const increment = () => {
      setCount(c => c + 1);
    };
  
    return(
      <>
      <p>{count}</p>
      <button onClick={increment}> +
      </button>
      </>
    )
  }

export default Button;
