import React, {useState} from 'react';
import './Carousel.css';
import { CarouselData } from './CarouselData';

const Carousel = ({slides}) => {
  const [current, setCurrent] = useState(0);
  const length = slides.length;

  if (!Array.isArray(slides) || slides.length <= 0){
    return null;
  }

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  }

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  }

  return (
    <>
    <div className="carousel-container">
    <img src="images/left-arrow.png" alt="" className="left-arrow" onClick={prevSlide} />
    {CarouselData.map((slide, index) => {
      <div className={index === current ? 'slide active' : 'active'} key={index}>
        {index === current && (
          <img src={slide.image} alt="" className='carousel-image' />
        )}
      </div>
    })}
    <img src="images/right-arrow.png" alt="" className="right-arrow" onClick={nextSlide} />
    </div>
    </>
  )
}

export default Carousel
